import React from 'react';
function App()
{
    const greeting={
        backgroundColor:'green',
        height:'100vh',
        width:'100%',
        display:'grid',
        alignItems:'center',
        justifyContent:'center'
      }
      const heading={
        backgroundColor:'white',
        borderRadius:'50%',
        padding:'20px'
      }
      let date= new Date();
    let hr = date.getHours();
    let message="";
    date=date.toLocaleTimeString();
    if(hr>=20 && hr<24)
    {
      message='good Night';
    }
    else if(hr>0 && hr<12)
    {
      message='good morning';
    }
    else if(hr>=12 && hr<17)
    {
      message='good afternoon';
    }
    else if(hr>=17 && hr<20)
    {
      message='good evening';
    }
    return
    <>
    <div style={greeting}>
      <h1 style={heading}>Hello friend, { message }<br/><span>Your time is {date}</span></h1>
    </div>
    </>
}

export default App;
